#include <stdio.h>
#include <string.h>

void test(char* directory)
{
	char filename[6235];
	char* p;
	char* q;

      for (p=directory; *p != '\0'; p++)
        {
          q=p;
          while ((*q != '\n') && (*q != '\0'))
            q++;
          (void) strncpy(filename,p,q-p);
          filename[q-p]='\0';
          p=q;
          (void) fprintf(stdout,"    %.1024s",filename);
        }
}

int main(void)
{
	test("");
}
